public:
    vector<string> cellsInRange(string s) {
        char c1 = s[0];
        char r1 = s[1];
        char c2 = s[3];
        char r2 = s[4];
        vector<string> ans;
        for(int i=0;i+c1<=c2;i++)
        {
            for(int j=0;j+r1<=r2;j++)
            {
                string res;
                // s = "K1:L2"
                // here c1 = k,c2 = L,r1 = 1,r2 = 2 ,i=j=0.;
