class Solution {
    public List<String> cellsInRange(String s) {
        // List to store the ans.
        List<String> list = new ArrayList<>();
        // StringBuilder to make the each String.
        StringBuilder sb = new StringBuilder();
        // Variables to store values 
        char c = s.charAt(0);
        int n = s.charAt(3) - c;
        int c1 = s.charAt(1)-'0';
        int m = (s.charAt(4)-'0')-c1;
        // It will iterate through all the columns.
        for(int i =0;i<=n;i++){
            // It will iterate through all the rows.
            for(int j=0;j<=m;j++){
                //First add column to the string then add row.
                sb.append((char)(c+i));
                sb.append(c1+j);
                // add the answer to the list.
                list.add(sb.toString());
                // It will make the StringBuilder empty.
                sb.setLength(0);
            }
        }
        // Finaly Return the answer.
        return list;
    }
}
