impl Solution {
    pub fn convert_time(current: String, correct: String) -> i32 {
        let current: Vec<_> = current.split(":").collect();
        let correct: Vec<_> = correct.split(":").collect();
        let start0: i32 = current[0].parse().unwrap();
        let start1: i32 = current[1].parse().unwrap();
        let end0: i32 = correct[0].parse().unwrap();
        let end1: i32 = correct[1].parse().unwrap();

        let mut diff = (end0 - start0) * 60 + (end1 - start1);
        let mut cnt = 0;
        for n in vec![60, 15, 5, 1] {
            if diff >= n {
                cnt += diff / n;
                diff %= n
            }
        }
        cnt
    }
}
