def convertTime(self, current, correct):
    current = int(current.split(":")[0])*60 + int(current.split(":")[1])
    correct = int(correct.split(":")[0])*60 + int(correct.split(":")[1])

    amount, coins = correct - current, [1,5,15,60]

    dp = [float("inf")]*(amount+1)

    dp[0] = 0

    for coin in coins:
        for i in range(coin, amount+1):
            dp[i] = min(dp[i], 1 + dp[i-coin])

    return dp[-1]

