var convertTime = function(current, correct) {
    const steps = [60, 15, 5, 1];
    const currentTimeInMinutes = convertToMinutes(current);
    const correctTimeInMinutes = convertToMinutes(correct);
    let diff = correctTimeInMinutes - currentTimeInMinutes;
    let operationsCount = 0;

    for (const step of steps) {
        const currentStepsCount = Math.floor(diff / step);
        operationsCount += currentStepsCount;
        diff -= currentStepsCount * step;
        if (diff === 0) break;
    }
    return operationsCount;
};

function convertToMinutes(time) {
    const [hours, minutes] = Array.from(time.split(':'), Number);
    return hours * 60 + minutes;
}
