public class Solution {
    public int ConvertTime(string current, string correct) {
        if (current == correct)
        {
            return 0;
        }

        int counter = 0;
        int correctHours = int.Parse(correct[..2]);
        int currentHours = int.Parse(current[..2]);
        int currentMinutes = int.Parse(current.Substring(3, 2));
        int correctMinutes = int.Parse(correct.Substring(3, 2)) + 60 * Math.Abs(correctHours - currentHours);

        while (currentMinutes != correctMinutes)
        {
            if ((currentMinutes + 60) <= correctMinutes)
            {
                currentMinutes += 60;
            }
            else if ((currentMinutes + 15) <= correctMinutes)
            {
                currentMinutes += 15;
            }
            else if ((currentMinutes + 5) <= correctMinutes)
            {
                currentMinutes += 5;
            }
            else if ((currentMinutes + 1) <= correctMinutes)
            {
                currentMinutes += 1;
            }

            counter++;
        }

        return counter;
    }
}
