function getmin(min1, min2, ops) {
  var A = min1;
  var B = min2;
  while (A < B && A + 15 <= B) {
    ops++;
    A += 15;
  }
  while (A < B && A + 5 <= B) {
    ops++;
    A += 5;
  }
  while (A < B) {
    ops++;
    A++;
  }
  return ops;
}

function getpair(input, arr) {
  var hours = [];
  var mins = [];
  hours.push(input[0], input[1]);
  mins.push(input[input.length - 2], input[input.length - 1]);
  var h = +hours.join("");
  var m = +mins.join("");
  arr.push([h, m]);
  return arr;
}

var convertTime = function (current, correct) {
  if (current === correct) {
    return 0;
  }
  var ops = 0;
  var arr = [];
  getpair(current, arr);
  getpair(correct, arr);
  var cur = arr[0];
  var cor = arr[1];
  var X = cur[1];
  var Y = cor[1];
  while (cor[0] > cur[0]) {
    ops++;
    cur[0]++;
  }
  if (cor[1] > cur[1]) {
    return getmin(X, Y, ops);
  }
  if (Y < X && cur[0] < cor[0]) {
    return getmin(X, Y + 60, ops);
  } else {
    if (cor[1] !== cur[1]) {
      return getmin(X, Y + 60, ops) - 1;
    }
  }
  return ops;
};
