class Solution {
public:
    long long maximumBeauty(vector<int>& flowers, long long newFlowers, int target, int full, int partial) {
        sort(flowers.begin(), flowers.end());
        if (target == flowers[0]) return flowers.size() * (long long)full;
        int n = flowers.size();
        long long max_beauty = 0;

        vector<long long> required_flower(n, 0);   //required_flower[i]: Number of flowers required to make first i gardens have at least flowers[i] flowers;
        for (int i = 1; i < n; ++i) {
            required_flower[i] = required_flower[i - 1] + i * (long long)(flowers[i] - flowers[i - 1]);
        }
        for (int j = n; j >= 0; --j) {
            if (j != n && flowers[j] < target) {                             //if j already full, we don't decrease avaialble flowers
                newFlowers -= (target - flowers[j]);
            }
            if (newFlowers <= -1) break;                                    //if number of avaiable flowers smaller than 0
            long long score = (n - j) * (long long)full;                //score of making gardens j to n - 1 full
            if (j == 0) {                                                                //if j is at index 0, we don't have any incomplete flowers
                max_beauty = max(score, max_beauty);
                break;
            }
            int candidate = max(0, binary_search(0, j - 1, newFlowers, required_flower));       
            long long max_flower = flowers[candidate] + (newFlowers - required_flower[candidate]) / (candidate + 1);    
            max_flower = min((long long)target - 1, max_flower);          //max flower of incomplete garden, can't be higher or equal to target
            max_beauty = max(score + (long long)max_flower * partial, max_beauty);
        }
        return max_beauty;
    }
    int binary_search(int left, int right, int n, vector<long long>& vec) {
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (n >= vec[mid]) left = mid + 1;
            else right = mid - 1;
        }
        return right;
    }
};
