class Solution {
    public long maximumBeauty(int[] flowers, long newFlowers, int target, int full, int partial) {
        Arrays.sort(flowers);
        long[] cost = new long[flowers.length + 1];
        long[] preSum = new long[flowers.length + 1];
        for (int i = 0; i < flowers.length; i++) {
            cost[i + 1] = cost[i] + Math.max(target - flowers[i], 0);
            preSum[i + 1] = preSum[i] + flowers[i];
        }
        if (flowers[0] >= target) {
            return (long)full * flowers.length;
        }
        long ans = 0;
        if (cost[flowers.length] - cost[0] <= newFlowers) {
            ans = (long)full * flowers.length;
        }
        for (int i = 0; i < flowers.length && flowers[i] < target; i++) {
            if (cost[flowers.length] - cost[i + 1] > newFlowers) continue;
            long remain = newFlowers - cost[flowers.length] + cost[i + 1];
            int left = 0, right = i;
            while (left <= right) {
                int mid = left + (right - left) / 2;
                if (remain >= (long)(mid + 1) * flowers[mid] - preSum[mid + 1]){
                    left = mid + 1;
                }else {
                    right = mid - 1;
                }
            }
            long part = Math.min(flowers[right] + (remain - (long)flowers[right] * (right + 1) + preSum[right + 1]) / (right + 1), target - 1);
            ans = Math.max(ans, part * partial + (long)full * (flowers.length - i - 1));
        }
        return ans;
    }
}
