use std::cmp::min;
use std::cmp::max;
use std::cmp::Ordering;

pub fn upper_bound<T: PartialOrd>(arr: &[T], target: T) -> usize {
    let mut l = 0;
    let mut r = arr.len();
    while l<r {
        let m = (l+r)/2;
        let v = unsafe {arr.get_unchecked(m)};
        if *v <= target {
            l = m + 1;
        }else{ //v > target
            r = m;
        }
    }
    //l==r
    l
}

pub fn lower_bound<T: PartialOrd>(arr: &[T], target: T) -> usize {
    let mut l = 0;
    let mut r = arr.len();
    while l<r {
        let m = (l+r)/2;
        let v = unsafe{arr.get_unchecked(m)};
        if *v >= target {
            r = m;
        }else{ //v < target
            l = m+1;
        }
    }
    //l==r
    l
}

