var maximumBeauty = function(flowers, newFlowers, target, full, partial) {
    flowers.sort((b, a) => a - b);

    let p1 = 0, ans = 0;

    while (target - flowers[p1] <= newFlowers)
        newFlowers -= Math.max(0, target - flowers[p1++]);

    if (p1 == flowers.length)
        return (p1 - 1) * full + Math.max(full, flowers.at(-1) < target ? (target - 1) * partial : 0);

    for (let p2 = flowers.length - 1, suffixSum = flowers[p2]; p1 >= 0 && target > flowers[p1]; ) {
        while (p1 < p2 && (flowers[p2 - 1] == flowers[p2] || suffixSum + newFlowers > (flowers.length - p2) * flowers[p2 - 1])) {
            suffixSum += flowers[--p2];
        }

        const incompleteFlowersSpan = flowers.length - p2;
        const leftWall = p2 == p1 ? target : flowers[p2 - 1];
        const minIncompleteFlowers = leftWall -
			Math.ceil((incompleteFlowersSpan * leftWall - (suffixSum + newFlowers)) / incompleteFlowersSpan);

        ans = Math.max(ans, p1 * full + minIncompleteFlowers * partial);
        newFlowers += target - flowers[--p1];
    }

    return ans;
};
