class Solution:
    def maximumBeauty(self, flowers: List[int], newFlowers: int, target: int, full: int, partial: int) -> int:
        flowers = sorted(flowers)[::-1]
        n = len(flowers)

        preSum = [0]
        for i in range(n-1, -1, -1):
            preSum.append(preSum[-1] + flowers[i])

        preSum = preSum[::-1]

        def findMin(left, numAdded):
            lo = flowers[-1]
            hi = target - 1

            def checkMin(value):
                a = left
                b = n - 1
                while(a <= b):
                    mid = (a + b)//2
                    if flowers[mid] >= value:
                        a = mid + 1
                    else:
                        b = mid - 1

                goodPoint = b + 1
                return preSum[goodPoint] + numAdded >= value *  (n - goodPoint)

            while(lo <= hi):
                mid = (lo + hi) // 2
                if checkMin(mid):
                    lo = mid + 1
                else:
                    hi = mid - 1

            return lo - 1

        start = n
        for i in range(n):
            if flowers[i] < target:
                start = i
                break

        rest = sum(flowers[start:])
        maxScore = start * full + ((findMin(start, newFlowers)) * partial if n-start > 0 else 0)
        while(start < n + 1):
            maxScore = max(maxScore, start * full + ((findMin(start, newFlowers)) * partial if n-start > 0 else 0))
            if start == n or newFlowers < target - flowers[start]:
                break
            else:
                rest -= flowers[start]
                newFlowers -= target - flowers[start]
                start += 1

        return maxScore
