class Solution:
    def maximumBeauty(self, flowers: List[int], newFlowers: int, target: int, full: int, partial: int) -> int:
        flowers_sorted = sorted(flowers)

        full_cnt = 0
        min_partial = 1000000
        prefix_sm = []

        for i, f in enumerate(flowers_sorted):
            if f >= target:
                full_cnt += 1
            else:
                min_partial = min(f, min_partial)

            if not prefix_sm:
                prefix_sm.append(0)
            else:
                nv = prefix_sm[-1] + ((f-flowers_sorted[i-1]) * len(prefix_sm))
                prefix_sm.append(nv)
        curr_score = full_cnt * full + partial * (0 if min_partial == 1000000 else min_partial)
        for f in range(full_cnt, len(flowers_sorted)+1):
            rem_flowers = self.calc_rem_flowers(f, flowers_sorted, newFlowers, target) if f > full_cnt else newFlowers
            if (rem_flowers <= 0):
                break
            min_partial = self.calc_min_partial(f, prefix_sm, flowers_sorted, rem_flowers)
            if min_partial >= target:
                min_partial = target - 1
            curr_score = max(curr_score, f * full + partial * min_partial)
            newFlowers = rem_flowers
        return curr_score

    def calc_rem_flowers(self, f, flowers_sorted, newFlowers, target):
        for i in range(len(flowers_sorted)-f, -1, -1):
            if target > flowers_sorted[i]:
                newFlowers = newFlowers - (target-flowers_sorted[i])
            if len(flowers_sorted) - i == f:
                return newFlowers
        return -1

    def calc_min_partial(self, f, prefix_sm, flowers_sorted, rem_flowers):
        # find the minimum value by distributing remaining flowers
        N = len(flowers_sorted)
        lo = 0
        hi = N-f
        idx = bisect.bisect_left(prefix_sm, rem_flowers, lo, hi)
        if idx == 0:
            # distributing 0 flowers
            return 0
        return flowers_sorted[idx-1] + (rem_flowers - prefix_sm[idx-1])//(idx-lo)
