long long maximumBeauty(vector<int> &flowers, long long newFlowers, int _target, int full, int partial) {
        long long target = _target;
        sort(flowers.begin(), flowers.end(), greater<int>());
        int n = flowers.size();
        long long min_full = 0;
        for (auto flower: flowers) {
            if (flower >= target)
                min_full++;
        }
        long long temp = newFlowers;
        long long max_full = 0;
        for (int i = 0; i < n; i++) {
            temp -= max((long long) 0, target - flowers[i]);
            if (temp < 0)
                break;
            max_full++;
        }
        vector<long long> partial_min_arr(n, 0);
        for (int i = n - 2; i >= 0; i--) {
            partial_min_arr[i] = partial_min_arr[i + 1] + (flowers[i] - flowers[i + 1]) * (n - 1 - i);
        }
        vector<long long> remain_by_num_full(n + 1, 0);
        temp = newFlowers;
        for (int i = min_full; i <= max_full; i++) {
            if (i != 0) {
                temp -= max((long long) 0, target - flowers[i - 1]);
            }
            remain_by_num_full[i] = max(temp, (long long) 0);
        }
        long long res = -1;
        for (long long num_full = min_full; num_full <= max_full; num_full++) {
            long long cur = full * num_full;
            long long remain = remain_by_num_full[num_full];
            long long partial_min_index = n - (upper_bound(partial_min_arr.rbegin(), partial_min_arr.rend(), remain)
                                               - partial_min_arr.rbegin());
            partial_min_index = max(partial_min_index, num_full);
            long long num_partial_fill = n - partial_min_index;
            long long num_partial = 0;
            if (num_partial_fill != 0)
                num_partial = min(
                        target - 1,
                        flowers[partial_min_index] + (remain - partial_min_arr[partial_min_index]) / num_partial_fill
                );
            cur += num_partial * partial;
            res = max(res, cur);
        }
        return res;
    }
