class Solution {
public:
    vector<long long> sum;
    int binarysearch(vector<int>& nums,int l,int h,long long m){
        int re = 0;
        while(l<=h){
            int mid = (l+h)/2;
            if(nums[mid]<m){ re = mid; l=mid+1; }
            else h=mid-1;
        }
        return re;
    }
    long long find(vector<int>& nums,long long t,long long r,int x){
        long long l = nums[0],h=t-1,ans = 0;
        while(l<=h){
            long long m = (l+h)/2;
            long long temp = 0;
            int k = binarysearch(nums,0,nums.size()-x-1,m);
            temp = m*(k+1)-sum[k];
            if(temp<=r){
                ans = m; l=m+1;
            }
            else h=m-1;
        }
        return ans;
    }
    long long maximumBeauty(vector<int>& nums, long long n, int t, int full, int partial) {
        sort(nums.begin(),nums.end());
        long long l = 0,h=0,temp=n; // l = min no. of full garden, h = Max no. of garden that can be full
        vector<long long> arr; 
        sum = vector<long long>(nums.size(),0); sum[0]=nums[0];
        for(int i = 1; i<nums.size();++i)sum[i]=nums[i]+sum[i-1];
        for(int i = nums.size()-1;i>=0;--i){
            if(nums[i]>=t) {l++;h++;}
            else{
                if(temp>=t-nums[i]){  // if ith garden is not full, Check if it can be completed 
                    h++; temp-=t-nums[i];  
                    arr.push_back(temp);  // Store the no. of flowers left after ith garden is completed
                }
                else break;
            }
        }
        long long ans = (long long)full*l;  // calculate ans for `l`
        if(l<nums.size()) ans+=(long long)partial*find(nums,t,n,l);
        for(long long i = l+1;i<=h;++i){
            long long p = 0;
            if(nums.size()>i) p=find(nums,t,arr[i-l-1],i); // Get the maximized minimum no. of flowers if `i` gardens are completed and arr[i-l-1] flowers are left
            p = i*full +p*partial;
            ans = max(ans,p);
        }
        return ans;
    }
