class Solution:
    def maximumBeauty(self, flowers: List[int], newFlowers: int, target: int, full: int, partial: int) -> int:
        flowers = sorted(flowers)
        n = len(flowers)
        i = n - 1
        while i >= 0 and flowers[i] >= target:
            i -= 1
        if i < 0:
            return n * full
        full_cnt = n - i - 1
        flowers = flowers[:i+1]
        n = len(flowers)
        psum, ssum = list(flowers), list(flowers)
        for i in range(1, n):
            psum[i] += psum[i - 1]       
        for i in range(-2, -(n+1), -1):
            ssum[i] += ssum[i + 1]

        max_score = flowers[0] * partial + full_cnt * full
        for min_flowers in range(flowers[0], target + 1):
            l1, r1 = 0, n - 1
            while l1 <= r1:
                m1 = (l1 + r1) // 2
                if flowers[m1] < min_flowers:
                    l1 = m1 + 1
                else:
                    r1 = m1 - 1
            l3, r3 = l1, n - 1
            while l3 <= r3:
                m3 = (l3 + r3) // 2
                if flowers[m3] <= min_flowers:
                    l3 = m3 + 1
                else:
                    r3 = m3 - 1
            remains = newFlowers if r1 < 0 else newFlowers - ((r1 + 1) * min_flowers - psum[r1])
            if remains < 0:
                break
            if min_flowers == target:
                score = full * (len(flowers) + full_cnt)
                max_score = max(score, max_score)
                break
            l2, r2 = l3, n - 1
            while l2 <= r2:
                m2 = (l2 + r2) // 2
                if (n  - m2) * target - ssum[m2] <= remains:
                    r2 = m2 - 1
                else:
                    l2 = m2 + 1

            full_cnt1 = full_cnt
            if l2 < n:
                remains -= ((n - l2) * target - ssum[l2])
                full_cnt1 += (n - l2)
            if l2 == l3 and remains > 0:
                full_cnt1 += min(remains // (target - min_flowers), l2 - 1)
            score = min_flowers * partial + full_cnt1 * full
            max_score = max(max_score, score)

        return max_score

