        unordered_map<int, int> mp;
        for(int& task : tasks) mp[task]++;

        int ans = 0;        
        for(auto [_, freq] : mp)
        {
            if(freq == 1) return -1;
            ans += (freq % 3 == 0) ? freq/3 : freq/3 + 1;
        }
        return ans;
    }
};
