    let dp = [Infinity, Infinity, 1, 1]; // 🛑 base cases for 0, 1, 2, 3
    let f = x => {
        while (dp.length <= x) {
            let n = dp.length;
            let a = dp[n - 2],
                b = dp[n - 3];
            dp.push(1 + Math.min(a, b));
        }
        return dp[x];
    };
    let t = [...m].map(([_, cnt]) => cnt).map(x => f(x));
