type VI = Vec<i32>;
use std::cmp::min;
use std::collections::HashMap;
impl Solution {
    pub fn minimum_rounds(A: VI) -> i32 {
        let INF = 1000000007;
        let mut m = HashMap::new();
        let mut dp = vec![INF, INF, 1, 1]; // 🛑 base cases for 0, 1, 2, 3
        for x in A {
            *m.entry(x).or_insert(0) += 1;
        }
        fn f(dp: &mut VI, x: i32) -> i32 {
            let x = x as usize;
            while dp.len() <= x {
                let n = dp.len();
                let a = dp[n - 2];
                let b = dp[n - 3];
                dp.push(1 + min(a, b));
            }
            dp[x]
        }
        let t: VI = m.into_values().map(|x| f(&mut dp, x)).collect();
        if t.iter().any(|&x| x == INF) { -1 } else { t.iter().sum() }
    }
}
