class Solution {
public:
    using VI = vector<int>;
    using Map = unordered_map<int, int>;
    int minimumRounds(VI& A, int INF = 1e9 + 7, Map m = {}, VI t = {}) {
        for (auto x: A)
            ++m[x];
        VI dp{ INF, INF, 1, 1 }; // 🛑 base cases for 0, 1, 2, 3
        auto f = [&](auto x) {
            while (dp.size() <= x) {
                int n = dp.size(),
                    a = dp[n - 2],
                    b = dp[n - 3];
                dp.push_back(1 + min(a, b));
            }
            return dp[x];
        };
        transform(m.begin(), m.end(), back_inserter(t), [&](auto& pair) {
            auto [_, x] = pair;
            return f(x);
        });
        if (any_of(t.begin(), t.end(), [=](auto x) { return x == INF; }))
            return -1;
        return accumulate(t.begin(), t.end(), 0);
    }
};
