    def minimumRounds(self, tasks: List[int]) -> int:
        # idea: greedy
        # find as many 3 as possible

        counter = Counter(tasks)
        steps = 0

        for key, value in counter.items():
            cur_step = self.composedBy(value)

            if cur_step == -1:
                return -1
            else:
                steps += cur_step

        return steps

    # find as many 3 as possible
