class Solution {
    public int minimumRounds(int[] tasks) {
        HashMap<Integer,Integer> hash = new HashMap<>();
        int ans = 0;

        for(int x:tasks)
            hash.put(x,hash.getOrDefault(x,0)+1);

        for (Map.Entry<Integer, Integer> me : hash.entrySet()) {
            int x = me.getValue();
            if(x==1)
                return -1;
            if (x % 3 == 0) 
                ans += x / 3;
            else if (x % 3 == 2) 
                ans += (x - 2) / 3 + 1;
            else
                ans += (x - 4) / 3 + 2;
        }
        return ans;
    }
}
