var minimumRounds = function(tasks) {
    <!-- map to store frequencies -->
    const hash = new Map();
    <!-- total count | answer  -->
    let count = 0;

    for(let task of tasks){
        <!-- if that number already exit in table than update it's value -->
        if(hash.has(task)){
            hash.set(task, hash.get(task) + 1)
        }else{
            <!-- if that number don't exit add ti table -->
            hash.set(task, 1)
        }
    }
    <!-- iterate over Map -->
    for(let [key, value] of hash){
        <!-- 
            if frequncy is 1 the its not possible to complete tasks
            so return -1 
        -->
        if(value <= 1) return -1

        <!-- if completely divisible to 3 than add directly to count -->
        if(value % 3 === 0){
            count += value/3
        }else{
        <!-- if not divisible with 3 -->
            count += Math.floor(value / 3) + 1
        }
    }

    return count;
};

```

