func minimumRounds(tasks []int) int {
    result, count := 0, make(map[int]int)

    for _, t := range tasks {
        count[t]++
    }
    for _, v := range count {
        for v > 0 {
            switch {
            case v % 3 == 0:
                result, v = result + v / 3, 0
            case v > 4:
                result++
                v -= 3
            case v % 2 == 0:
                result, v = result + v / 2, 0
            case v == 1:
                return -1
            }
        }
    }

    return result
}
