'''
from collections import Counter

class Solution:

    def minimumRounds(self, tasks: List[int]) -> int:
        task_ct = Counter(tasks)

		# Since has to be 2 or 3 at a time, any occurrence of 1 will return -1
        if 1 in task_ct.values():
            return(-1)

        output = 0

        for i in task_ct.values():

			# 3 at a time is the quickest
            this_round = i//3

			# If there's a leftover 2, it counts as well
			# If there's a leftover 1, it won't affect the situation
			#     we can just "borrow" 1 from any of the 3 so there are two 2s
            if i%3 != 0:
                this_round += 1
            output += this_round

        return(output)

