class Solution {
public:
    int dfs(vector<vector<int>>& x,int& mx,int i,string& s){
        if(x[i].size()==0) return 1;
        vector<int> e;
        int mx1=0,mx2=0;
        for(int j=0;j<x[i].size();j++){
            int a = dfs(x,mx,x[i][j],s);
            if(s[x[i][j]]==s[i]) a=0;
            if(a>mx1){
                mx2=mx1;
                mx1=a;
            }
            else if(a>mx2) mx2=a;
