class Solution {
public:
    int maxi = 0;
    int dfs(int i, int par, vector<int> adj[], string &s) {
        int ans = 1;
        vector<int>curs;
        for(auto &j : adj[i]) {
            if(j != par){
                int cur = dfs(j, i, adj, s);
                if(s[i] != s[j])cur++;
                else cur = 0;
                ans = max(ans, cur);
                curs.push_back(cur);
            }
        }
        int sz = curs.size();
        sort(begin(curs), end(curs));
        if(sz > 1) maxi = max(maxi, curs[sz-1] + curs[sz-2] - 1);
        maxi = max(maxi, ans);
        return ans;
    }

    int longestPath(vector<int>& parent, string s) {
        int n = parent.size();
        vector<int> adj[n];
        for(int i = 1; i < parent.size(); i++) {
            adj[parent[i]].push_back(i);
            adj[i].push_back(parent[i]);
        } 
        dfs(0, -1,  adj, s);
        return maxi;
    }
};
