class Solution {
public:
    // declared here, no need to pass in recursive functions
    vector<int>* children; 
    string s;
    int maxSoFar = 1;

    int recur(int node)
    {
        int n = children[node].size();

        int first = 0;      // first highest path in my subtree
        int second = 0;     // second hihghest path in my subtree

        for(int i = 0; i<n; i++)
        { 
            int ele = children[node][i];
            int smallAns = recur(ele);

            if(s[ele] == s[node])   // no need to consider if child has same aplphabet
                continue;

            // compete candidate soln with first and second highest
            if(smallAns < second)
                continue;

            // now candt >= second
            if(smallAns < first)
            {
                second = smallAns;
                continue;
            }

            // now candt >= first
            second = first;
            first = smallAns;
        }

        int myGlobAns = first + second + 1;
        if(myGlobAns > maxSoFar)
            maxSoFar = myGlobAns;

        return first + 1;   // retuned answer is diff from myGlobAns
    }
    int longestPath(vector<int>& parent, string s) {
        int n = parent.size();

        // these are global/ class level, no need to pass in functions
        this->s = s;
        children = new vector<int>[n];

        // converting parent array to children array
        for(int i = 1; i<n; i++)
            children[parent[i]].push_back(i);

        // just call the function with starting node
        recur(0);
        return maxSoFar;
    }
};
```

