class Solution {
public:
    unordered_map<int, vector<int>> d;
    unordered_map<int, int> vis;
    int fans = 0;

    int dfs(int root){

        if(vis[root] == 1) return 0;
        vis[root] = 1;

        vector<int> ansx = {0, 0};

        for(int i: d[root]){
            if(vis[i] == 0)
            ansx.push_back(dfs(i));
        }

        sort(ansx.begin(), ansx.end());
        int ans = 1 + ansx.back() + ansx[ansx.size()-2];
        fans = max(fans, ans);
        return 1 + ansx.back();

    }

    int longestPath(vector<int>& parent, string s) {
        for(int i=0;i<parent.size();i++){
            if(parent[i] != -1 && s[i] != s[parent[i]]){
                d[i].push_back(parent[i]);
                d[parent[i]].push_back(i);
            }
        }

        for(int i=0;i<parent.size();i++){
            dfs(i);
        }

        return fans;

    }
};
