class Solution {
    int ans;
    public int longestPath(int[] parent, String s) {
        int n = parent.length;
        List<List<Integer>> store = new ArrayList<>();
		//creating a graph
        for(int i = 0; i < n; i++) {
            store.add(new ArrayList<Integer>());
        }
		//adding values to graph
        for(int i = 1; i < n; i++) {
            store.get(parent[i]).add(i);
        }
		//storing minimum value to ans
        ans = Integer.MIN_VALUE;
        dfs(store, 0, s);
        return ans;
    }

    public int dfs(List<List<Integer>> graph, int curr, String s) {
        int max1 = 0, max2 = 0; // storing maximum and 2nd maximum value

		//traversing throuogh all childs of 'curr'
        for(int i : graph.get(curr)) {
            int st = dfs(graph, i, s);
            if(s.charAt(curr) != s.charAt(i))
			//storing maximum and 2nd maximum value
				if(st>max1) {
					max2 = max1;
					max1 = st;
				} else if(st > max2) {
					max2 = st;
				}
        }
		//adding 1 -> to count the contribution of node also
        ans = Math.max(max1+max2+1, ans);
		//returning the maximum path (to reach a leaf node)
        return max1+1;
    }
}
