Build `graph`. Build `memo` which is the longest path starting at `node` and not going back to `from`.
Complexity: O(V + E) = O(n)
```
class Solution {
    vector<vector<int>> graph;
    vector<unordered_map<int, int>> memo;
    int find(int node, int from, char ch, const string &s) {
        if (s[node] == ch) {
            return 0;
        }
        if (memo[node].find(from) != memo[node].end()) {
            return memo[node][from];
        }
        int result = 0;
        for (int next : graph[node]) {
            if (next == from) {
                continue;
            }
            result = max(result, find(next, node, s[node], s));
        }
        result++;
        memo[node][from] = result;
        return result;
    }
public:
    int longestPath(vector<int>& parent, string s) {
        int n = parent.size();
        graph = vector<vector<int>>(n);
        memo = vector<unordered_map<int, int>>(n);
        for (int i = 1; i < n; i++) {
            graph[parent[i]].push_back(i);
            graph[i].push_back(parent[i]);
        }
        int result = 0;
        for (int i = 0; i < n; i++) {
            result = max(result, find(i, -1, '0', s));
        }
        return result;
    }
};
