class Solution {
public:
    int result;
    int dfs(int root,int parent,unordered_map<int,vector<int>> &adj,string &s){
        int largest=0;
        int secondLargest=0;

        for(auto &child:adj[root]){
            if(child==parent){
                continue;
            }

            int child_longest_path=dfs(child,root,adj,s);

            if(s[child]==s[root]){
                continue;
            }
            if(child_longest_path>secondLargest){
                secondLargest=child_longest_path;
            }
            if(secondLargest>largest){
                swap(largest,secondLargest);
            }
        }
            int ansIsRoot=1;
            int ansLeftOrRight=max(largest,secondLargest)+1;
            int ansIsDownTheCurr=largest+secondLargest+1;

            result=max({result,ansIsRoot,ansLeftOrRight,ansIsDownTheCurr});

            return max(ansIsRoot,ansLeftOrRight);

