    }
    int longestPath(vector<int>& parent, string s) {

        unordered_map<int,vector<int>> adj;
        for(int i=1;i<parent.size();i++){
            int u=i;
            int v=parent[i];
            adj[u].push_back(v);
            adj[v].push_back(u);
        }
        result=0;
        dfs(0,-1,adj,s);
        return result;
    }
};
