class Solution {
    int n,ans; vector<vector<int>> graph;
    int dfs(int root, string &s,int par){
        priority_queue<int, vector<int>, greater<int>> pq;  // this pq will store top 2 longest path from all the child
        for(int child:graph[root]){
            pq.push(dfs(child,s,root));
            if(pq.size()>2) pq.pop();
        }
        if(!(pq.size()))  return s[root]==s[par] ? 0 : 1;  // this is the base case for leaf node
        int mx=0, temp = 1;  // now we will take temp max ans ans we will take max = mx from all the result stored in pq to get the max from all the children node 
        while(pq.size()) {
            mx = max(mx,pq.top()), 
            temp +=pq.top(); // this will get max from top 2 element in pq and give the current max at the current node
            pq.pop();
        }
        ans = max(temp,ans); // store the max 
        return s[root]==s[par] ? 0 : mx+1; //  if root amd par have different dtring value then we will return max from all the child node + 1 = max + 1
    }
