            }
        }
        //to consider the current node
        maxpath += 1;
        //updating the maximum path length so far
        maxval = max(maxval, maxpath + smaxpath);
        //returning only the maximum path from subtrees
        return maxpath;
    }

    int longestPath(vector<int>& parent, string s) {
        vector<vector<int>> tree(parent.size());
        //adjacency list for tree
        for(int i = 1; i < parent.size(); i++) {
            tree[i].push_back(parent[i]);
            tree[parent[i]].push_back(i);
        }
        maxval = 0;
        dfs(tree, 0, s, -1);
        return maxval;
