            temp= solve(adj, x, start, s);
            if(temp!=-1) // if them isn't -1 then we found a path
            // from the current node where we can go down
            {
                if(temp>=mx1) //update mx1 and mx2
                {
                   mx2=mx1;
                   mx1=temp;
                }
                else if(temp<mx1 && temp>mx2)
                {
                   mx2=temp;
                }
            }
        }
        ans=max(ans, mx1+mx2+1); //update ans as mx1+mx2+1 ( 1 for 
                                   //current node)
        if(parent==-1)
        return mx1+1;
        if(s[start]==s[parent]) // if(child has the same character
    // as parent return -1 because parent cannot proceed in this direction)
        return -1;
        else
        return mx1+1;
    }
    int longestPath(vector<int>& parent, string s) {
        int n=parent.size();
        vector<vector<int>>adj(n);
        for(int i=1;i<n;i++) // create adjacency list 
        {
            adj[parent[i]].push_back(i);
        }
        solve(adj, 0 ,-1,s);
        return ans;
    }
};
