class Solution(object):
    def __init__(self):
        self.ans = 0

    def longestPath(self, parent, s):
        n = len(parent)
        subtrees = [[] for _ in range(n)]

        for i in range(1, n):
            subtrees[parent[i]].append(i)

        self.dfs(0, -1, subtrees, s)

        return self.ans

    def dfs(self, node, parent, subtrees, s):
        max_val = 0

        for child in subtrees[node]:
            if child == parent:
                continue
            part = self.dfs(child, node, subtrees, s)
            self.ans = max(self.ans, part + max_val + 1)
            max_val = max(max_val, part)

        max_val += 1

        if max_val > self.ans:
            self.ans = max_val

        if parent != -1 and s[parent] == s[node]:
            return 0

        return max_val

