```rust []
impl Solution {
    pub fn longest_path(parent: Vec<i32>, s: String) -> i32 {
        let n = parent.len() as usize;

        let mut subtree = vec![Vec::new(); n];
        let s = s.into_bytes();
        let mut ans = 0;

        for i in 1..n {
            subtree[parent[i] as usize].push(i);
        }

        Self::dfs(0, 100000, &subtree, &s, &mut ans);

        ans
    }

    fn dfs(node: usize, parent: usize, subtree: &Vec<Vec<usize>>, s: &Vec<u8>, ans: &mut i32) -> i32 {
        let mut max = 0;
        for child in subtree[node].iter() {
            if child == &parent {
                continue;
            }
            let part = Self::dfs(*child, node, subtree, s, ans);
            *ans = i32::max(*ans, part + max + 1);

            max = max.max(part);
        }

        max += 1;

        if max > *ans {
            *ans = max;
        }

        if parent != 100000 && s[parent] == s[node] {
            return 0
        }

        max
    }
}

