class Solution {
public:
    vector<int> indp,outdp;
    vector<vector<int>> adj;
    vector<pair<int,int>> maxVal;

    int n;
    void dfs1(int node,int par,string &s){

         indp[node]=0;
         int mx =0;
         priority_queue<int,vector<int>,greater<int>> pq;
         pq.push(0);
         pq.push(0);
         for(auto ch:adj[node]){
             if(ch == par) continue;
             dfs1(ch,node,s);
             if(s[node] != s[ch]){
                 mx=max(mx,indp[ch]);
                 pq.push(indp[ch]);
                 if(pq.size() > 2) pq.pop();
             }
         }

         int max2 = pq.top();
         pq.pop();
         int max1 = pq.top();
         maxVal[node] = {max1,max2};

         indp[node] = mx+1;
    }

    void dfs2(int node,int par,string &s){

          if(par == -1 ) outdp[node] = 1;
          else{
              if(s[node] == s[par]) outdp[node] = 1;
              else{
                  if(outdp[par]>indp[par]) outdp[node] = outdp[par]+1;
                  else{
                      pair<int,int> mxv = maxVal[par];
                      if(mxv.F > indp[node])  outdp[node] = indp[par]+1;
                      else{
                          outdp[node] =  max(outdp[par] + 1, mxv.S == 0 ? 0: mxv.S + 2);
                      }
                  }
              }
          }
          for(auto ch : adj[node]){
              if(ch == par) continue;
              dfs2(ch,node,s);
          }
    }

    int longestPath(vector<int>& parent, string s) {
        n=s.size();
        indp.resize(n);
        outdp.resize(n);
        adj.resize(n);
        maxVal.resize(n);

        for(int i=1;i<parent.size();i++){
            adj[i].push_back(parent[i]);
            adj[parent[i]].push_back(i);
        }

        dfs1(0,-1,s);
        dfs2(0,-1,s);
        int ans=0;


        for(int i=0;i<n;i++) ans = max(ans,indp[i] + outdp[i]-1);
        return ans;
    }
};
