class Solution {
public:
    int res = 0;
    int recurse(int nd, int prev, vector<vector<int>>&t, string &s){
        int m1 = 0,m2=0;
        for(int i = 0; i < t[nd].size(); i++){
            if(t[nd][i] == prev) continue;
            int temp = recurse(t[nd][i],nd,t,s);
            if(temp > m1){
                m2 = m1;
                m1 = temp;
            }
            else if(temp > m2){
                m2 = temp;
            }
        }
        res = max(res,m1+m2);
        if(prev>=0 && s[nd] == s[prev]) return 0;
        return m1+1;
    }
    int longestPath(vector<int>& parent, string s) {
        int n = s.length();
        vector<vector<int>> adj(n);
        for(int i = 1; i < parent.size(); i++){
            adj[i].push_back(parent[i]);
            adj[parent[i]].push_back(i);
        }
        recurse(0,-1,adj,s);
        return res+1;
    }
};
