class Solution {
public:
    int dis[100005];
    int ans=1;
    void dfs(int src,vector<vector<int>>& adjLs,string& s){
        dis[src]=1;
        for(int node:adjLs[src]){
            dfs(node,adjLs,s);
            if(s[src]!=s[node]){
                ans=max(ans,dis[src]+dis[node]);
                dis[src]=max(dis[src],dis[node]+1);
            }
        }
