class Solution {
public:
    int res = 1;
    int dfs(vector<int> adj[], string &str, int s, int parent){
        int len = 1;
        priority_queue<int> pq;
        for(auto x: adj[s]){
            if(x != parent){
                int len1 = dfs(adj, str, x, s);
                if(str[x] != str[s]){
                    pq.push(len1);
                }
            }
        }
        int one = 0, two = 0;
        if(pq.size() > 0){
            one = pq.top(); pq.pop();
            if(pq.size() > 0)
            two = pq.top(); pq.pop();
        }
        int len2 = len;
        len = one + two + 1;
        res = max(res, len);
        return len2 + max( one, two);
