        int n = s.length();
        vector<int> adj[n];
        for(int i = 1; i < n; i++){ int x = parent[i];
            adj[i].push_back(x);
            adj[x].push_back(i);
        }
        dfs(adj, s, 0, -1);
        return res;
    }
};
