     pii bfs(int s,vector<int> adj[],vector<int>& dis, vector<int> &vis,vector<int> &parent) {
        queue<int> q;
        q.push(s);
        vis[s] = 1;
        pii mx;
        mx.first = 0;
        mx.second = s;
        dis[s] = 0;
        while(!q.empty()) {
            int u = q.front();
            q.pop();
            for(auto v: adj[u]) {
                if(!vis[v]) {
                    dis[v] = dis[u] + 1;
                    vis[v] = 1;
                    q.push(v);
                    if(dis[v]>=mx.first){
                        mx.first = dis[v];
                        mx.second = v;
                    }
                }

            }
        }
        return mx;
    }
    int longestPath(vector<int>& parent, string s) {
        int n = parent.size();
        vector<int> adj[n];
        vector<int> dis(n,0);
        vector<int> vis(n,0);
        vector<int> vis1(n,0);
        for(int i=1;i<parent.size();i++) {
            if(s[i] != s[parent[i]]) {   // If adjacent nodes have different characters then include in the adjacency list otherwise don't.
                adj[i].push_back(parent[i]);
                adj[parent[i]].push_back(i);
            }
        }

        int mx = 0;
        for(int i=0;i<n;i++){
            if(vis[i]) continue;
            // Two BFS to calculate tree diameter
            pii root = bfs(i,adj,dis,vis,parent);   
            pii ans = bfs(root.second,adj,dis,vis1,parent);

            // Maximum tree diameter
            mx = max(mx,ans.first);
        }

        return mx+1;
    }
};
