    public class Graph {
		private int v;
		private char[] labels;
		private ArrayList<ArrayList<Integer>> adj;

		private int longestPath;

		public Graph(int v, char[] labels) {
			this.v = v;
			this.labels = labels;
			this.adj = new ArrayList<>(this.v);
			for (int i = 0; i < this.v; i++) {
				this.adj.add(new ArrayList<Integer>());
			}
		}

		public void addEdge(int u, int v) {
			adj.get(u).add(v);
		}

		public int dfsUtil(int parent) {
			int large = Integer.MIN_VALUE;
			int secondLarge = Integer.MIN_VALUE;
			for (int child : adj.get(parent)) {
				int childMax = dfsUtil(child);
                if (labels[parent] == labels[child]) {
                    continue;
                }
				if (labels[parent] != labels[child]) {
					if (childMax > large) {
						secondLarge = large;
						large = childMax;
					} else if (childMax > secondLarge) {
						secondLarge = childMax;
					}
				}
			}
			longestPath = Math.max(longestPath, large + secondLarge + 1);
			return large + 1;
		}

		public void dfs() {
			dfsUtil(0);
		}
	}

	public int longestPath(int[] parent, String s) {
		Graph graph = new Graph(parent.length, s.toCharArray());
		for (int i = 0; i < parent.length; i++) {
			if (parent[i] != -1) {
				graph.addEdge(parent[i], i);
			}
		}
