class Solution {
public:
    vector<vector<int>>adj;
    vector<int>mp;
    int ans=0;
    int longestPath(vector<int>& parent, string s) {
        //we are going to make a directed graph adj with the help of parent vector
        //we are going to calculate longest path from every vertex and use it to make
        //answer.For this we are going to perform dfs starting from 0 vertex.
        adj.resize(parent.size());
        mp.resize(s.length(),0);
        //directed graph parent->child
        for(int i=1;i<parent.size();i++)
        {
            int u=parent[i];
            int v=i;
            adj[u].push_back(v);
        }
        dfs(0,s);
        return ans;
    }
    void dfs(int src,string &s)
    {
        //every vertex is going to make its own path
        mp[src]=1;
        for(auto it=adj[src].begin();it!=adj[src].end();it++)
        {
            dfs(*it,s);
            //on returning we are calculating max path length
            if(s[src]!=s[*it])
            {
                ans=max(ans,mp[src]+mp[*it]);
                mp[src]=max(mp[src],mp[*it]+1);
            }
        }
        ans=max(ans,mp[src]);
    }
};
