/*
Problem in which at every node we decide whether to take the inverted V or not
If we are taking the inverted V then update the answer if possible and pass the longest
length that we could form to the above parent and for that parent too check if inverted V gives
max answer.
*/

class Solution {
public:
    int pathLen = 1;
    int dist[100005];

    void dfs(int src, vector<int> adj[], string &s) {
        dist[src] = 1;

        for(auto node : adj[src]) {
            dfs(node, adj, s);

            // returning
            if(s[src] != s[node]) {
                // Checking if inverted V is giving max or not
                pathLen = max(pathLen, dist[src] + dist[node]);
                // updating the node with the longest continuous straight path length 
                // we could achieve 
                dist[src] = max(dist[src], dist[node] + 1);
            }
        }
    }

    int longestPath(vector<int>& parent, string s) {
        int n = parent.size();
        vector<int> adj[n];

        for(int i=1; i<n; i++) {
            adj[parent[i]].push_back(i);
        }

        dfs(0, adj, s);

        return pathLen;
    }
};
