class Solution {
public:
    int res=0;
    int solve(int cur,vector<vector<int>> &adj,string &s)
    {
        int lsp=0;

        int p=-1,q=-1; //to store longest and second-longest paths in current subtree 
        for(auto &a:adj[cur]){
            int tmp=solve(a,adj,s);
            if(s[a]==s[cur]) tmp=0;
            if(tmp>=p){
                q=p;
                p=tmp;
            }
            else if(tmp>q) q=tmp;

        }
        if(p!=-1 && q!=-1) res=max(res,1+p+q);

        if(p==-1) lsp=1;
        else lsp=1+p;
        res=max(lsp,res);
        return lsp;

    }
    int longestPath(vector<int>& parent, string s) {
        int n=parent.size();
        if(n==1) return 1;
        vector<vector<int>> adj(n);
        for(int i=1;i<n;i++){
            adj[parent[i]].push_back(i);
        }
        int k=solve(0,adj,s);
        res=max(res,k);
        return res;
    }
};
