class Solution
{
public:
	int solve(int i, vector<vector<int>> &adj, int &ans, string &s)
	{
		int fi = -1, sec = -1;
		for (auto &x : adj[i])
		{
			int ch = solve(x, adj, ans, s);
			if (s[x] == s[i])
				continue;
			if (fi == -1)
				fi = ch;
			else if (fi <= ch)
			{
				sec = fi;
				fi = ch;
			}
			else
				sec = max(sec, ch);
		}
		ans = max(ans, fi + 1);
		ans = max(ans, sec + 1);
		ans = max(ans, fi + 1 + sec);
		if (fi == -1)
			return 1;
		return fi + 1;
	}
	int longestPath(vector<int> &parent, string s)
	{
		int n = parent.size();
		vector<vector<int>> adj(n);
		for (int i = 1; i < n; i++)
		{
			adj[parent[i]].push_back(i);
		}
		int ans = 1;
		solve(0, adj, ans, s);
		return ans;
	}
};
