// Approach: Depth First Search (DFS)

// Time complexity: O(n)
// Space complexity: O(n)

const longestPath = function(parent, s) {
	const graph = new Map();
	let longestPath = 1;

	for (let i = 1; i < parent.length; i++) {
		if (graph.has(parent[i]))
			graph.get(parent[i]).push(i);
		else
			graph.set(parent[i], [i]);
	}

	function dfs(curr) {
		if (!graph.has(curr))
